function no_cache_enqueue_style($slug, $file) {
	wp_enqueue_style($slug, plugin_dir_url( __FILE__ ) . $file . '?' . filemtime(plugin_dir_path(__FILE__) . $file));
}

function no_cache_enqueue_script($slug, $file, $deps = array()) {
    wp_enqueue_script($slug, plugin_dir_url( __FILE__ ) . $file . '?' . filemtime(plugin_dir_path(__FILE__) . $file), $deps);
}


add_action( 'wp_enqueue_scripts', 'negishut_enqueue' );
function negishut_enqueue() {
	no_cache_enqueue_style('webion-acc', 'css/webion_accessibility.css');
	no_cache_enqueue_script('webion-acc', 'js/webion_accessibility.js', array('jquery'));
}

